function divWheel(event) {
  if (event.wheelDelta > 0 || event.detail < 0) {
    if (event.currentTarget.scrollTop == 0) {
      modalOpen = false;
    }
  } else {
    if (event.currentTarget.scrollHeight - event.currentTarget.scrollTop == event.currentTarget.offsetHeight) {
      modalOpen = false;
    }
  }
}

