function extractTextWithTags(htmlString,n) {
    let count = 0;
    let result = '';
    for (let i = 0; i < htmlString.length; i++) {
        const char = htmlString[i];
        result += char;
        if (char === '<') {
            const endIndex = htmlString.indexOf('>', i);
            result += htmlString.substring(i + 1, endIndex + 1);
            i = endIndex;
        } else {
            count++;
        }
        if (count === n) {
            break;
        }
    }
    return result;
}
