function hasNumber(myString) {
  return /\d*\.?\d+$/.test(myString);
}

function hasDblQuote(myString) {
  return /[“||"]/.test(myString);
}

function hasSingleQuote(myString) {
  return /['||‘]/.test(myString);
}

