async function searchJobTitleLoc(inputsearchCategory,event){
    searchMode = "suggest";
    const minCharacters = 0;
    const currentinputbx = event.target;
    const searchField = inputsearchCategory;
    const suggesionList = currentinputbx.closest('.inputFieldBox').nextElementSibling;
    clearSuggestions = true;
    let inputfield = currentinputbx.value.split(',').pop().trim().toLowerCase();
    inputfield = inputfield.replace(/[#|;]/g,"");

    const jobURL = "/bin/tata/jobPostingsFilterServlet?"
    const jobParam = "searchTerm=" + encodeURIComponent(inputfield.replace(/[’’]/g, "'").replace(/[“”]/g, "\"").replace(/[#|;]/g,"")) + "&searchField=" + searchField + "&searchMode=" + searchMode;
    if(inputfield && inputfield.length > minCharacters){
        const searchJobXHR = new XMLHttpRequest();
        searchJobXHR.abort();
        searchJobXHR.onreadystatechange = function(){
            if (this.readyState === 4 && this.status == 200) {
                const response = JSON.parse(this.response);
                let items = response.response; 
                suggesionList.children[0].innerHTML = "";
                if(items.length > 0 && clearSuggestions == true){
                    suggesionList.classList.add("active");
                    suggesionList.scrollTop = 0;
                    items.forEach((item)=>{
                        const listItem = document.createElement('li');
                        listItem.innerHTML = item;
                        listItem.classList.add('jobSuggLabel');

                        listItem.addEventListener('click',(e)=>{
                            const currentValue = currentinputbx.value;
                            const values = currentValue.split(',');               
                            if(searchField === "jobTitle"){
                                dataLayer.push({
                                    event: "GAEventSuggestionTitle",
                                    eventCategory: "Suggestion selection",
                                    eventAction: "Selected joblisting title suggestion",
                                    eventLabel: listItem.textContent
                                });
                                values[values.length - 1] = listItem.textContent;
                                currentinputbx.value = values.map(title => title.trim()).join(", ") + ", ";
                                selectedJobTitle.clear();
                                values.forEach(title => {
                                    title = title.replace(regexForChars, '');
                                    selectedJobTitle.add(title.trim());
                                });
                            }else{
                                dataLayer.push({
                                    event: "GAEventSuggestionLocation",
                                    eventCategory: "Suggestion selection",
                                    eventAction: "Selected joblisting location suggestion",
                                    eventLabel: listItem.textContent
                                });
                                var [city,country] = listItem.textContent.split(',');
                                const inputvalues = currentValue.split(',').slice(0,-1);//taking all element excluding last element
                                //Code for flexible
                                if (city.trim() == "Flexible"){
                                city = city+" ("+country.trim()+")";
                    }
                                inputvalues.push(city);
                                currentinputbx.value = inputvalues.map(location => location.trim()).join(", ") + ", ";
                                updateSelectedLocations(currentinputbx.value);
                            }
                            
                            suggesionList.children[0].innerHTML = "";
                            currentinputbx.focus();
                            suggesionList.classList.remove("active");
                        });
                        suggesionList.children[0].appendChild(listItem);
                    });
                }else{
                    suggesionList.classList.remove("active");
                    suggesionList.children[0].innerHTML = "";
                }
            }
        };
        searchJobXHR.open("POST", jobURL, true);
        searchJobXHR.setRequestHeader('Content-type', 'application/x-www-form-urlencoded');
        searchJobXHR.send(jobParam);
    }
    else{
        suggesionList.classList.remove("active");
    }
    document.addEventListener('click', (e)=>{
        closeSuggetions(e);
    });
};
