//function to make ajax Calling
function makeRequest(url, postParameters) {
    return new Promise(function (resolve, reject) {
        let xhttp = new XMLHttpRequest();
        xhttp.open("POST", url, true);
        xhttp.setRequestHeader('Content-type', 'application/x-www-form-urlencoded');

        xhttp.onload = function () {
            try {
                if (this.status == 200) { 
                    let response = JSON.parse(xhttp.response);
                    totalCount = response.response.totalJobPostingsCount;
                    
                    resolve(response);
                } else {
                    reject({
                        status: this.status,
                        statusText: xhttp.statusText
                    });
                }
            } catch (error) {
               
            }

        };
        xhttp.onerror = function () {
            reject({
                status: this.status,
                statusText: xhttp.statusText
            });
        };
        model.lastQuery = postParameters;
        postParameters = postParameters + "&searchMode="+ searchMode;
//added code for passing only one time filterfFlag and start value
        var removefilterStart;
        var poststringStart = /(filtersFlag=(True|False)&start=\d+&)/;
        var filterStart = /filtersFlag=(True|False)&start=\d+&/g;
        var checkFirstString = postParameters.match(poststringStart);
        if(checkFirstString){
            removefilterStart = postParameters.replace(filterStart, '');
            removefilterStart = checkFirstString[0] + removefilterStart;
        }else{
            removefilterStart = postParameters;
        }
        model.lastUrl = url;
        xhttp.send(removefilterStart + ((removefilterStart.length > 0) ? "&jobFormat=" : "jobFormat=") + jobFormat);
    });
}


