//creation of jobs cards
function createHtmlForJobList(startNum, endNum, jobListing) {
    let jobList = [];
    let jobTitle, jobID, jobLoc, jobdesc, companyName, companyLogo, jobDetailUrl, jobDate;
    for (let i = startNum; i < endNum; i++) {
        jobTitle = jobListing[i].jobTitle;
        jobID = jobListing[i].jobId;
        jobLoc = jobListing[i].location;
        jobdesc = jobListing[i].shortDescription ? jobListing[i].shortDescription : "";
        jobDate = jobListing[i].publishedDate;
        companyName = jobListing[i].companyName;
        companyLogo = "" + jobListing[i].companyLogo;
        jobDetailUrl = document.getElementsByClassName("detailPageUrl")[0].getAttribute("href");
        let redirectionUrl = jobDetailUrl ? jobDetailUrl + "?jobId=" + encodeURIComponent(jobID) + "&company=" + encodeURIComponent(companyName) + "&jobTitle=" + encodeURIComponent(jobTitle) + "&location=" + encodeURIComponent(jobLoc) : "";
        if (jobdesc.length >= 195) {
            jobdesc = jobListing[i].shortDescription.substring(0, 195) + '...';
        }
        let Card = jobCardTemplate.cloneNode(true);
        Card.getElementsByClassName("anchorLinkCol")[0].href = redirectionUrl;
        Card.getElementsByTagName("IMG")[0].src = companyLogo;
        Card.getElementsByClassName("jobTitle")[0].innerText = jobTitle;
        Card.getElementsByClassName("details")[0].children[0].innerText = companyName;
        Card.getElementsByClassName("details")[0].children[1].innerText = jobLoc;
        Card.getElementsByClassName("jobDate")[0].innerText = jobDate;
        Card.getElementsByClassName("jobDesc")[0].innerText = jobdesc;
        jobList.push(Card);
    }
    return jobList;
}
