function showMoreFilterTabs(){
    loadMoreFilter = document.getElementsByClassName('loadMoreFilter')[0];
    let downArrrowTab = document.getElementsByClassName('downArrrowTab')[0];
    if(loadMoreFilter){
        if(loadMoreFilter.classList.contains('showLess')){
            for(var i = 5 ; i < appliedFilterList.children.length ; i++){
                appliedFilterList.children[i].classList.remove('hideFilterTab');
            }
            appliedFilterList.removeChild(loadMoreFilter);
            appliedFilterList.appendChild(loadMoreFilter);         
            downArrrowTab.style.transform = "rotate(0deg)";
            loadMoreFilter.classList.remove('showLess');
        }else{
            loadMoreFilter.classList.add('showLess');
            for(var i = 5 ; i < appliedFilterList.children.length ; i++){
                if(!appliedFilterList.children[i].classList.contains('loadMoreFilter')){
                    appliedFilterList.children[i].classList.add('hideFilterTab');
                }
            }
            appliedFilterList.removeChild(loadMoreFilter);
            downArrrowTab.style.transform = "rotate(180deg)";
            if(appliedFilterList.children.length > 6 && window.innerWidth < 767){
                appliedFilterList.insertBefore(loadMoreFilter, appliedFilterList.children[5]);
            }
        }  
        //for mob toggle the tabs dropdown button color transperent into blue like blink
        loadMoreFilter.addEventListener("touchstart", touchLoadmoreTabs);
        loadMoreFilter.addEventListener("touchend", touchLessTabs);
        loadMoreFilter.addEventListener("touchcancel", touchLessTabs);  
    }
    function touchLoadmoreTabs(e){
        loadMoreFilter.classList.add("tabBackgroundChange");
        downArrrowTab.setAttribute('src','/etc.clientlibs/tata/clientlibs/assets/resources/img/common/next_white.svg');
    } 
    function touchLessTabs(e){
        loadMoreFilter.classList.remove("tabBackgroundChange");
        downArrrowTab.setAttribute('src','/etc.clientlibs/tata/clientlibs/assets/resources/img/common/next01.svg');
    }  

}
var disclaimerStrip = document.getElementsByClassName("strip");
var jobAlertStrip = document.getElementsByClassName("jobAlertStrip");
function errorStatusCode(flag) {
    let searchSections = [...document.getElementsByClassName("searchDataSec")];
    if(disclaimerStrip.length > 0){
        searchSections.push(document.getElementsByClassName("strip")[0]);
    }
    if(jobAlertStrip.length > 0){
        searchSections.push(document.getElementsByClassName("jobAlertStrip")[0]);
    }
    let errorSection = document.getElementsByClassName("errSec")[0];
    let disclaimer = document.getElementsByClassName("disclaimer")[0];
    if (flag == true) {
        for (let section of searchSections) {
            section.style.display = "none";
        }
        errorSection.style.display = "block";
        disclaimer.style.display = "none";
        appliedFilterNote.style.display = "none";
    }
    else {
        for (let section of searchSections) {
            if (!section.classList.contains("noRes")){
                section.style.display = "block";
            }       
        }
        errorSection.style.display = "none";
        disclaimer.style.display = "block";
    }
}
